jQuery(document).ready(function() {
	jQuery(".skill-bar").each(function() {
		var el, progress;
		el = jQuery(this);
		progress = el.data("complete");
		return setTimeout(function() {
			return el.find(".progress-complete").animate({
				width: progress + "%"
			}, 800);
		}, 1500);
	});
});





